mapper.select:
		select  `APP_ID`, `LOAD_URL`,`COMBINE_APP_ID`,`COMBINE_TIME`,`ICON_URL`,`CODE_QR_A`,`CODE_QR_B`,`CODE_QR_C`,`CREATE_TIME`,`MODIFY_TIME`,`APP_NAME`,`APP_KEY`,`BUNDLE_NAME`,`BUNDLE_ID`,`SHORT_URL`,`EXPIRER_TIME`,`APP_TYPE`,`IS_COMBINE`,`DESCRIPTION` from d_app

			 and APP_ID != #{appId} 
		   and LOAD_URL = #{loadUrl} 
		   and COMBINE_APP_ID = #{combineAppId} 
		   and COMBINE_TIME = #{combineTime} 
		   and ICON_URL = #{iconUrl} 
		   and CODE_QR_A = #{codeQrA} 
		   and CODE_QR_B = #{codeQrB} 
		   and CODE_QR_C = #{codeQrC} 
		   and CREATE_TIME = #{createTime} 
		   and MODIFY_TIME = #{modifyTime} 
		   and APP_NAME = #{appName} 
		   and APP_KEY = #{appKey} 
		   and BUNDLE_NAME = #{bundleName} 
		   and BUNDLE_ID = #{bundleId} 
		   and SHORT_URL = #{shortUrl} 
		   and EXPIRER_TIME = #{expirerTime} 
		   and APP_TYPE = #{appType} 
		   and IS_COMBINE = #{isCombine} 
		   and DESCRIPTION = #{description} 



                order by ${sort} ${order}


                order by APP_ID desc



			limit #{offset}, #{limit}

