mapper.select:
		select `ORGANID`,`ORGANPID`,`ORGANNAME`,`AREAID`,`ORGANCODE`,`ORGANALIAS`,`POSTCODE`,`ADDRESS`,`TELEPHONE`,`FAX`,`ISVALIDATION`,`ORGANMAN`,`ORGANMANID`,`REMARK` from sys_organ

		  		   and ORGANID = #{organid} 
		  		   and ORGANPID = #{organpid} 
		  		   and ORGANNAME = #{organname} 
		  		   and AREAID = #{areaid} 
		  		   and ORGANCODE = #{organcode} 
		  		   and ORGANALIAS = #{organalias} 
		  		   and POSTCODE = #{postcode} 
		  		   and ADDRESS = #{address} 
		  		   and TELEPHONE = #{telephone} 
		  		   and FAX = #{fax} 
		  		   and ISVALIDATION = #{isvalidation} 
		  		   and ORGANMAN = #{organman} 
		  		   and ORGANMANID = #{organmanid} 
		  		   and REMARK = #{remark} 



                order by ${sort} ${order}


                order by ORGANID desc



			limit #{offset}, #{limit}

select count(*) from sys_organ

  		   and ORGANID = #{organid} 
  		   and ORGANPID = #{organpid} 
