mapper.select:
select `DEPTID`,`ORGANID`,`DEPTPID`,`DEPTNAME`,`ORDERNUM`,`DELFLAG`,`ISVALIDATION` from SYS_DEPARTMENT

	 and DEPTID = #{deptid} 
	 and ORGANID = #{organid} 
	 and DEPTPID = #{deptpid} 
	 and DEPTNAME like concat('%',#{deptname},'%')
	 and ORDERNUM = #{ordernum} 
	 and DELFLAG = #{delflag} 
	 and ISVALIDATION = #{isvalidation} 



		order by ${sort} ${order}


		order by DEPTID desc



	limit #{offset}, #{limit}

select count(*) from SYS_DEPARTMENT

	 and DEPTID = #{deptid} 
	 and ORGANID = #{organid} 
	 and DEPTPID = #{deptpid} 
	 and DEPTNAME = #{deptname} 
	 and ORDERNUM = #{ordernum} 
	 and DELFLAG = #{delflag} 
	 and ISVALIDATION = #{isvalidation}

select deptid,deptname,deptpid,organid from SYS_DEPARTMENT


	and isvalidation = 1 and deptpid = #{deptpid} 

	and isvalidation = 1 and deptpid = '' 

	 and organid = #{organid}

select deptid as id,deptname as text,organid as parentid  from SYS_DEPARTMENT


	and isvalidation = 1 and deptpid = '' 

	 and organid = #{organid}

select deptid as id,deptname as text,deptpid as parentid from SYS_DEPARTMENT


	and isvalidation = 1 and deptpid = #{deptpid} 

	 and organid = #{organid}
