mapper.select:
select `DEPTID`,`ORGANID`,`DEPTPID`,`DEPTNAME`,`ORDERNUM`,`DELFLAG`,`ISVALIDATION`,`DEPTCODE`,`REMARK`,`DEPTMAN`,`DEPTMANID` from sys_dept

	 and DEPTID = #{deptid} 
	 and ORGANID = #{organid} 
	 and DEPTPID = #{deptpid} 
	 and DEPTNAME like concat('%',#{deptname},'%')
	 and ORDERNUM = #{ordernum} 
	 and DELFLAG = #{delflag} 
	 and ISVALIDATION = #{isvalidation} 
	 and DEPTCODE = #{deptcode} 
	 and REMARK = #{remark} 
	 and DEPTMAN = #{deptman} 
	 and DEPTMANID = #{deptmanid} 



		order by ${sort} ${order}


		order by DEPTID desc



	limit #{offset}, #{limit}

select count(*) from sys_dept

	 and DEPTID = #{deptid} 
	 and ORGANID = #{organid} 
	 and DEPTPID = #{deptpid} 
	 and DEPTNAME = #{deptname} 
	 and ORDERNUM = #{ordernum} 
	 and DELFLAG = #{delflag} 
	 and ISVALIDATION = #{isvalidation} 
	 and DEPTCODE = #{deptcode} 
	 and REMARK = #{remark} 
	 and DEPTMAN = #{deptman} 
	 and DEPTMANID = #{deptmanid}

select deptid,deptname,deptpid,organid from sys_dept


	and isvalidation = 1 and deptpid = #{deptpid} 

	and isvalidation = 1 and deptpid = '' 

	 and organid = #{organid}

select deptid as id,deptname as text,organid as parentid  from sys_dept


	and isvalidation = 1 and deptpid = '' 

	 and organid = #{organid}

select deptid as id,deptname as text,deptpid as parentid from sys_dept


	and isvalidation = 1 and deptpid = #{deptpid} 

	 and organid = #{organid}
